import React from "react";
import Button from 'react-bootstrap/Button';


const Form = (props) => {

    const inputTextHandler = (e) => {
        props.setInputText(e.target.value);
    };
    const submitHandler = (e) => {
        e.preventDefault();
        props.setTodos([
            ...props.todos, {text: props.inputText}
        ]);
        props.setInputText("");
    };

    return(
        <div className= "container">
            <form className= "form">
                <div className="input-group">
                    <div className="input-group-prepend">
                        <span className="input-group-text" id="basic-addon">
                        <i className="fa fa-user prefix"></i>
                        </span>
                    </div>
                    <input value={props.inputText} onChange={inputTextHandler} className= "todo-input form-control" type="text" placeholder="Task to do"/>
                </div>
                <Button onClick= {submitHandler} className= "todo-button" type= "submit" variant="primary">+</Button>
                
                

                {/* <div className= "select">
                    <select name="todos" id="todos" className= "filter-todo">
                        <option value="all">All</option>
                        <option value="Completed">Completed</option>
                        <option value="Uncompleted">Uncompleted</option>
                    </select>
                </div> */}
            </form>
        </div>
    );
};

export default Form;
