import React from "react";
import Button from 'react-bootstrap/Button';


const Form = (props) => {

    const inputTextHandler = (e) => {
        console.log(e.target.value);
        props.setInputText(e.target.value);
    };
    const submitHandler = (e) => {
        e.preventDefault();
        props.setTodos([
            ...props.todos, {text: props.inputText}
        ]);
        props.setInputText("");
    };

    return(
         <form className= "form">
             <input value={props.inputText} onChange={inputTextHandler} type="text" className= "todo-input"/>
             <button onClick={submitHandler} className= "todo-button" type= "submit">+</button>
             <Button variant="outline-primary">Primary</Button>{' '}

             <div className= "select">
                <select name="todos" id="todos" className= "filter-todo">
                    <option value="all">All</option>
                    <option value="Completed">Completed</option>
                    <option value="Uncompleted">Uncompleted</option>
                </select>
             </div>
         </form>
    );
};

export default Form;
